    def calculate(self):
            price = int(self.menu_select.currentText().split("/")[1].strip())
            foodquantity = int(self.quantity_select.currentText())
            total = (foodquantity * price)
            self.Total_text.setText("Total " + str(total) + "฿") 
        #show order selected in label
    def show_order_selected(self):
            self.menu_picked.setText(f"{self.menu_select.currentText()} * {self.quantity_select.currentText()}")
            
